const wasm = require('./')
    , fs   = require('fs')


function wasmLoader (module, filename) {
  var wasmData = fs.readFileSync(filename)
  var wasmJs   = wasm(null, wasmData)
  wasmJs       = 'module.exports=' + wasmJs
  return module._compile(wasmJs, filename)
}


require.extensions['.wasm'] = wasmLoader
